import { DATA_COMMENT, UPDATE_COMMENT } from "./action";

const initialState = {
  data: {
    comment_id: 0,
    comment_name: "",
    comment_rating: 0,
    comment_text: "",
  },
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case DATA_COMMENT:
      return { ...state.data, ...action.payload };
    case UPDATE_COMMENT:
      const { field, value } = action.payload;
      return { ...state.data, [field]: value };
    default:
      return state;
  }
};

export default reducer;
