import React, { useEffect, useState } from "react";
import "../../assets/css/register.css";
import "../../assets/css/bootstrap.min.css";
import logo from "../../assets/logo/jababeka_report.png";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useQueryClient, useMutation } from "react-query";
import { registerUser } from "../../api/apiUser";
import { onInputChange } from "../shared/utils";

const Register = () => {
  const queryClient = useQueryClient();
  const navigate = useNavigate();
  const [wrongPass, setWrongPass] = useState("");
  const [formRegister, setFormRegister] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    repeat_password: "",
  });

  const {
    status: statusRegisterUser,
    error: errorRegisterUser,
    mutate: handleRegister,
  } = useMutation({
    mutationFn: async () =>
      await registerUser(
        formRegister.username,
        formRegister.email,
        formRegister.phone,
        formRegister.password
      ),
    onSuccess: (data) => {
      if (data.data.loginStatus !== "none") {
        localStorage.setItem("data", JSON.stringify(data.data.data[0]));
        navigate("/");
      } else {
        setWrongPass("Sorry your username or password is wrong");
      }
      return queryClient.invalidateQueries(["comments"]);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post(process.env.REACT_APP_BACKEND_PORT + "/api/user/filterRegister", {
        user_name: formRegister.username,
        user_email: formRegister.email,
        user_phone: formRegister.phone,
      })
      .then((res) => {
        if (formRegister.password === formRegister.repeat_password) {
          if (res.data.desc === "none") {
            axios
              .post(process.env.REACT_APP_BACKEND_PORT + "/api/user/add", {
                user_name: formRegister.username,
                user_email: formRegister.email,
                user_phone: formRegister.phone,
                user_password: formRegister.password,
              })
              .then((res) => {
                navigate("/login");
              })
              .catch((err) => console.log(err));
          } else {
            setWrongPass("Your username or email or phone is already exist");
          }
        } else {
          setWrongPass("Your password and confirm password is not same");
        }
      })
      .catch((err) => console.log(err));
  };

  return (
    <div className="container_register">
      <div className="square_register">
        <div className="line_logo">
          <img className="img_logo_register mt-5" src={logo} />
        </div>
        <h2 className="text_login mt-3">Register</h2>

        <div className="form_login p-4">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-lg-6">
                <div>
                  <label htmlFor="username" className="form-label">
                    Username
                  </label>

                  <input
                    type="text"
                    className="form-control"
                    id="username"
                    placeholder="Your username"
                    value={formRegister.username}
                    onChange={(e) =>
                      onInputChange(
                        "username",
                        e,
                        formRegister,
                        setFormRegister
                      )
                    }
                  />
                </div>

                <div>
                  <label htmlFor="Email" className="form-label mt-3">
                    Email
                  </label>

                  <input
                    type="email"
                    className="form-control"
                    id="Email"
                    placeholder="Your email"
                    value={formRegister.email}
                    onChange={(e) =>
                      onInputChange("email", e, formRegister, setFormRegister)
                    }
                  />
                </div>

                <div>
                  <label htmlFor="phone" className="form-label mt-3">
                    Phone Number
                  </label>

                  <input
                    type="text"
                    className="form-control"
                    id="phone"
                    placeholder="Your phone number"
                    value={formRegister.phone}
                    onChange={(e) =>
                      onInputChange("phone", e, formRegister, setFormRegister)
                    }
                  />
                </div>
              </div>

              <div className="col-lg-6">
                <div>
                  <label htmlFor="password" className="form-label">
                    Password
                  </label>

                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    placeholder="Your password"
                    value={formRegister.password}
                    onChange={(e) =>
                      onInputChange(
                        "password",
                        e,
                        formRegister,
                        setFormRegister
                      )
                    }
                  />
                </div>

                <div>
                  <label htmlFor="repeat_password" className="form-label mt-3">
                    Confirm Password
                  </label>

                  <input
                    type="password"
                    className="form-control"
                    id="repeat_password"
                    placeholder="Confirm your password"
                    value={formRegister.repeat_password}
                    onChange={(e) =>
                      onInputChange(
                        "repeat_password",
                        e,
                        formRegister,
                        setFormRegister
                      )
                    }
                  />
                </div>
              </div>
            </div>

            <button className="btn btn-login btn-danger mt-4">Sign Up</button>
          </form>
        </div>

        <p className="text-danger">{wrongPass}</p>

        <p className="pb-1">
          You have account?
          <a href="/login" className="text_signUp">
            Sign In
          </a>
        </p>
      </div>
    </div>
  );
};

export default Register;
