import { DATA_COMMENT } from "./action";

const initialState = {
  dataComment: [],
};

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_INVENTORY:
      return [...state, action.payload];
    case INCREASE_ITEM:
      return increaseItem(state, action.payload);
    case DECREASE_ITEM:
      return decreaseItem(state, action.payload);
    case DELETE_INVENTORY:
      const newState = [...state];
      return newState.filter((item) => item.item_id !== action.payload);
    case DELETE_ALL_INVENTORY:
      return state.filter((item) => item.item_id !== item.item_id);
    default:
      return state;
  }
};
