import React, { useEffect, useState } from "react";
import "../../assets/css/bootstrap.min.css";
import "bootstrap";
import logo from "../../assets/logo/jababeka_report_white.png";
import profile from "../../assets/logo/account.png";
import image from "../../assets/image/image.png";
import "../../assets/css/home.css";
import ClipLoader from "react-spinners/ClipLoader";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faLocationArrow,
  faMapMarkerAlt,
  faPencilAlt,
  faSave,
  faSignOutAlt,
  faSyncAlt,
  faUserCircle,
  faWindowClose,
} from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import ReactPaginate from "react-paginate";
import ListReports from "./ListReports";

const ListReport = ({
  id,
  author,
  text,
  location,
  status,
  date,
  imageReport,
  initSession,
  dataComment,
  bool,
}) => {
  const [commentId, setCommentId] = useState(0);
  const [commentRating, setCommentRating] = useState(0);
  const [commentText, setCommentText] = useState("");
  const urlBackend = "http://localhost:4000";
  const [messageInput, setMessageInput] = useState("");
  const [show, setShow] = useState(false);
  const [pict, setPict] = useState(null);
  const [file, setFile] = useState(null);
  const [listSelect, setListSelect] = useState({
    text: "",
    location: "",
  });

  const selectGiveComment = (item) => {
    setCommentRating(item.comment_rating);
    setCommentId(item.comment_id);
    setCommentText(item.comment_text);
  };

  const onInputChange = (input, value) => {
    setListSelect({
      ...listSelect,
      [input]: value.target.value,
    });
  };

  const handleFile = (e) => {
    let file = e.target.files[0];
    setFile(file);
    setPict(URL.createObjectURL(file));
  };

  const selectDescription = () => {
    setListSelect({
      text: text,
      location: location,
    });

    if (!show) setShow(true);
    else setShow(false);
  };

  const convertTimeToJs = (date) => {
    let a = date + "";
    let b = a.slice(0, 10).replace("T", " ");

    let splt = b.split("");

    let d = splt.slice(0, 4);
    let e = splt.slice(5, 7);
    let f = splt.slice(8, 10);

    let month = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "Mei",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Okt",
      "Nov",
      "Dec",
    ];
    let combine = d.join("") + "/" + e.join("") + "/" + f.join("");
    let lastResult = new Date(combine);

    let result =
      f.join("") + " " + month[lastResult.getMonth()] + ", " + d.join("");
    return result;
  };

  const enterUpdate = (e) => {
    e.preventDefault();

    if (file) {
      let formData = new FormData();
      console.log("file", file);

      formData.append("post_text_report", listSelect.post_text_report);
      formData.append("post_location", listSelect.post_location);
      formData.append("post_image", file);

      // for (const property in listSelect) {
      //   formData.append(`${property}`, listSelect[property]);
      // }

      axios
        .put(urlBackend + "/api/post/updateImage/" + id, formData)
        .then((res) => {
          console.log("success updated", res);
          bool(true);
          setShow(false);
        })
        .catch((err) => console.log(err));
    } else {
      axios
        .put(urlBackend + "/api/post/update/" + id, {
          post_text_report: listSelect.text,
          post_location: listSelect.location,
        })
        .then((res) => {
          console.log("success updated", res);
          bool(true);
          setShow(false);
        })
        .catch((err) => console.log(err));
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();

    axios
      .post(urlBackend + "/api/comment/add", {
        comment_name: "Admin",
        comment_text: messageInput,
        post_id: id,
      })
      .then((res) => {
        console.log("succes", res);
        // setDataComment([
        //   ...dataComment,
        //   {
        //     comment_name: "Admin",
        //     comment_text: messageInput,
        //     post_id: id,
        //   },
        // ]);
        bool(true);
        setMessageInput("");
      })
      .catch((err) => console.log(err));
  };

  const updateRating = () => {
    axios
      .put(urlBackend + "/api/comment/update/" + commentId, {
        comment_text: commentText,
        comment_rating: commentRating,
      })
      .then((res) => {
        bool(true);
        console.log("commentId", commentId);
        console.log("comment_text", commentText);
        console.log("success updated", res);
      })
      .catch((err) => console.log(err));
  };

  const changeStatus = (status) => {
    if (status === "Waiting")
      axios
        .put(urlBackend + "/api/post/changeStatus/" + id, {
          post_status: "Waiting",
        })
        .then((res) => {
          console.log("success updated", res);
          bool(true);
          setShow(false);
        })
        .catch((err) => console.log(err));
    else
      axios
        .put(urlBackend + "/api/post/changeStatus/" + id, {
          post_status: "Finish",
        })
        .then((res) => {
          console.log("success updated", res);
          bool(true);
          setShow(false);
        })
        .catch((err) => console.log(err));
  };

  return (
    <div className="wrap_item">
      <div className="row main_report">
        <div className="col-lg-6">
          {initSession === "admin" ? (
            <img className="image_report" src={image} />
          ) : (
            <div className="image-upload">
              <label htmlFor="file-input">
                {pict ? <img src={pict} /> : <img src={image} />}
              </label>

              {/* {show && (
                <input
                  id="file-input"
                  type="file"
                  onChange={(e) => handleFile(e)}
                />
              )} */}
            </div>
          )}
        </div>
        <div className="col-lg-6 mt-5">
          <div className="top_report">
            <h2>{author}</h2>
            <h2>{convertTimeToJs(date)}</h2>

            {initSession === author &&
              (show ? (
                <>
                  <FontAwesomeIcon
                    onClick={() => setShow(false)}
                    className="mt-2 ml-1 windowClose"
                    icon={faWindowClose}
                  />
                </>
              ) : (
                <>
                  <FontAwesomeIcon
                    onClick={() => selectDescription()}
                    className="mt-1 mr-2"
                    icon={faPencilAlt}
                  />
                </>
              ))}
          </div>

          {show ? (
            <>
              <textarea
                className="form-control mt-2 text_area_description"
                value={listSelect.text}
                onChange={(e) => onInputChange("text", e)}
                rows="4"
                cols="50"
              />
              <input
                className="form-control mt-2 location_description"
                onChange={(e) => onInputChange("location", e)}
                value={listSelect.location}
              />

              <button
                className="btn btn-danger save_description mt-2"
                onClick={(e) => enterUpdate(e)}
              >
                <FontAwesomeIcon className="windowSave" icon={faSave} />
              </button>
            </>
          ) : (
            <>
              <div className="main_change">
                <p className="text_report mt-2">{text}</p>
                <div className="location_report">
                  <FontAwesomeIcon
                    className="icon_location "
                    icon={faMapMarkerAlt}
                  />
                  <p className="ml-5 text_location">{location}</p>
                </div>

                <div className="bar_status">
                  {status &&
                    (status === "Waiting" ? (
                      <>
                        <div className="bar_status_belum">
                          <p className="ml-5 status_progress">{status}</p>
                        </div>

                        {initSession === "admin" && (
                          <FontAwesomeIcon
                            onClick={() => changeStatus("Finish")}
                            className="ml-2 change_status"
                            icon={faSyncAlt}
                          />
                        )}
                      </>
                    ) : (
                      <>
                        <div className="bar_status_sudah">
                          <p className="ml-5 status_progress">{status}</p>
                        </div>

                        {initSession === "admin" && (
                          <FontAwesomeIcon
                            onClick={() => changeStatus("Waiting")}
                            className="ml-2 change_status"
                            icon={faSyncAlt}
                          />
                        )}
                      </>
                    ))}
                </div>
              </div>
            </>
          )}
        </div>
      </div>

      {(dataComment || []).map((item) => {
        if (item.post_id === id) {
          return (
            <ListComments
              key={item.comment_id}
              name={item.comment_name}
              text={item.comment_text}
              itemAll={item}
              initSession={initSession}
              dataComment={dataComment}
              star={() => selectGiveComment(item)}
              submit={() => updateRating()}
              bool={bool}
              commentRating={(e) => setCommentRating(e)}
              commentValRating={commentRating}
            />
          );
        }
      })}

      {initSession === "admin" && (
        <div className="input-group">
          <hr />
          <span className="input-group-text bg-icon">
            <FontAwesomeIcon className="icon-left" icon={faUserCircle} />
          </span>
          <input
            type="text"
            className="form-control "
            placeholder="Send your message"
            aria-label="Recipient's username"
            aria-describedby="button-addon2"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
          />
          <button
            className="btn btn-send"
            type="button"
            id="button-addon2"
            onClick={(e) => sendMessage(e)}
          >
            <FontAwesomeIcon className="icon-btn" icon={faLocationArrow} />
          </button>
        </div>
      )}
    </div>
  );
};

const Home = () => {
  const initSession = JSON.parse(localStorage.getItem("data")) || 0;
  const backLogin = "http://localhost:3000/login";
  const urlBackend = "http://localhost:4000";
  const [loading, setLoading] = useState(false);
  const [bool, setBool] = useState(false);
  const [dataComment, setDataComment] = useState([]);
  const [dataReport, setDataReport] = useState([]);
  const [currentItems, setCurrentItems] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [itemOffset, setItemOffset] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(3);
  const [selectCategory, setSelectCategory] = useState("Accident");
  const [listCategory, setListCategory] = useState([
    { id: 1, category: "Accident" },
    { id: 2, category: "Criminal" },
    { id: 3, category: "Environment" },
    { id: 4, category: "Infrastructure" },
  ]);

  useEffect(async () => {
    if (initSession) {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 100);
    } else {
      window.location.href = backLogin;
    }

    initSession.user_name === "admin"
      ? await axios
          .post(urlBackend + "/api/post/listAll", {
            post_category: selectCategory,
          })
          .then((res) => {
            console.log("post", res);
            setDataReport(res.data.data);

            let data = res.data.data;
            const endOffset = itemOffset + itemsPerPage;
            setCurrentItems(data.slice(itemOffset, endOffset));
            setPageCount(Math.ceil(data.length / itemsPerPage));
          })
          .catch((err) => console.log(err))
      : await axios
          .post(urlBackend + "/api/post/listDetail", {
            post_category: selectCategory,
            user_id: initSession.user_id,
          })
          .then((res) => {
            setDataReport(res.data.data);

            let data = res.data.data;
            const endOffset = itemOffset + itemsPerPage;
            setCurrentItems(data.slice(itemOffset, endOffset));
            setPageCount(Math.ceil(data.length / itemsPerPage));
          })
          .catch((err) => console.log(err));

    await axios
      .get(urlBackend + "/api/comment/list")
      .then((res) => {
        setDataComment(res.data.data || 0);
      })
      .catch((err) => console.log(err));

    {
      bool && setBool(false);
    }
  }, [itemOffset, itemsPerPage, bool, selectCategory]);

  const handlePageClick = (e) => {
    const newOffset = (e.selected * itemsPerPage) % dataReport.length;
    console.log(
      `User requested page number ${e.selected}, which is offset ${newOffset}`
    );
    setItemOffset(newOffset);
  };

  const handleLogout = () => {
    localStorage.clear();
    localStorage.removeItem("data");
    window.location.href = backLogin;
  };

  const enterInput = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      alert("input");
    }
  };

  console.log(initSession);

  return (
    <div className="container_home">
      {loading ? (
        <>
          <ClipLoader color={"#fa2626"} loading={"#F37A24"} size={50} />
        </>
      ) : (
        <div className="column">
          <nav className="navbar navbar-expand-lg">
            <div className="container-fluid">
              <a className="mt-3">
                <img className="img_logo_home" src={logo} />
              </a>

              <button
                className="navbar-toggler"
                type="button"
                data-bs-toggle="collapse"
                data-bs-target="#navbarSupportedContent"
                aria-controls="navbarSupportedContent"
                aria-expanded="false"
                aria-label="Toggle navigation"
              >
                <span className="navbar-toggler-icon"></span>
              </button>
              <div
                className="collapse navbar-collapse"
                id="navbarSupportedContent"
              >
                <ul className="navbar-nav me-auto mt-3">
                  <li className="nav-item input_li">
                    {/* <form className="d-flex">
                      <input
                        className="form-control input_search"
                        type="search"
                        placeholder="You can search report on here"
                        aria-label="Search"
                        aria-describedby="basic-search"
                        onKeyDown={(e) => enterInput(e)}
                      />
                      <span
                        className="input-group-text icon-search icon-font"
                        id="basic-search"
                      >
                        <FontAwesomeIcon
                          className="text-white"
                          icon={faSearch}
                        />
                      </span>
                    </form> */}
                  </li>

                  <li className="nav-item account">
                    <div className="wrap_account">
                      <p className="name_profile mt-2 text-white">
                        {initSession.user_name}
                      </p>

                      <img className="img_profile " src={profile} />

                      <FontAwesomeIcon
                        onClick={() => handleLogout()}
                        className="logout text-white"
                        icon={faSignOutAlt}
                      />
                    </div>
                  </li>
                </ul>
              </div>
            </div>
          </nav>

          <div className="main">
            <section id="category">
              <div className="parents_category">
                {listCategory.map((item) => {
                  if (item.category === selectCategory) {
                    return (
                      <button
                        onClick={() => setSelectCategory(item.category)}
                        className="item_category btn item_active"
                      >
                        {item.category}
                      </button>
                    );
                  } else {
                    return (
                      <button
                        onClick={() => setSelectCategory(item.category)}
                        className="item_category btn border border-danger text-danger"
                      >
                        {item.category}
                      </button>
                    );
                  }
                })}
              </div>
            </section>

            <section id="list_report">
              <div className="container_report">
                {dataReport.length > 0 ? null : (
                  <p className="fw-bold mt-5">You don't have data</p>
                )}

                {(currentItems || []).map((item) => {
                  if (selectCategory === item.post_category)
                    if (initSession.user_id === item.user_id) {
                      return (
                        <ListReport
                          key={item.post_id}
                          id={item.post_id}
                          author={item.post_author}
                          text={item.post_text_report}
                          location={item.post_location}
                          date={item.post_date}
                          status={item.post_status}
                          imageReport={item.post_image}
                          initSession={initSession.user_name}
                          dataComment={dataComment}
                          bool={(e) => setBool(e)}
                        />
                      );
                    } else {
                      return (
                        <ListReport
                          key={item.post_id}
                          id={item.post_id}
                          author={item.post_author}
                          text={item.post_text_report}
                          location={item.post_location}
                          date={item.post_date}
                          status={item.post_status}
                          imageReport={item.post_image}
                          initSession={initSession.user_name}
                          dataComment={dataComment}
                          bool={(e) => setBool(e)}
                        />
                      );
                    }
                })}
              </div>
              <div className="row parent_paginate">
                <div className="paginate">
                  <ReactPaginate
                    breakLabel="..."
                    nextLabel="Next"
                    onPageChange={handlePageClick}
                    pageRangeDisplayed={2}
                    pageCount={pageCount}
                    previousLabel="Previous"
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}
                    renderOnZeroPageCount={null}
                  />
                </div>
              </div>
            </section>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
