import { DATA_COMMENT } from "./action";

const initialState = [];

const reducer = (state = initialState, action) => {
  
  switch (action.type) {
    case DATA_COMMENT:
      return [...state, action.payload];
    default:
      return state;
  }
};

export default reducer;
