import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";

let utils = {
  formulaRating: (number) => {
    let arr = [];
    if (number > 0)
      if (number <= 20) {
        return (
          <FontAwesomeIcon
            className="text-warning icon_ratings"
            icon={faStar}
          />
        );
      } else if (number >= 21 && number <= 40) {
        for (let i = 1; i <= 2; i++) {
          arr.push(
            <FontAwesomeIcon
              key={i}
              className="text-warning icon_ratings"
              icon={faStar}
            />
          );
        }
        return arr;
      } else if (number >= 41 && number <= 60) {
        for (let i = 1; i <= 3; i++) {
          arr.push(
            <FontAwesomeIcon
              key={i}
              className="text-warning icon_ratings"
              icon={faStar}
            />
          );
        }
        return arr;
      } else if (number >= 61 && number <= 80) {
        for (let i = 1; i <= 4; i++) {
          arr.push(
            <FontAwesomeIcon
              key={i}
              className="text-warning icon_ratings"
              icon={faStar}
            />
          );
        }
        return arr;
      } else {
        for (let i = 1; i <= 5; i++) {
          arr.push(
            <FontAwesomeIcon
              key={i}
              className="text-warning icon_ratings"
              icon={faStar}
            />
          );
        }
        return arr;
      }
  },
};

module.exports = utils;
