import React, { useEffect, useState } from "react";
import ClipLoader from "react-spinners/ClipLoader";
import axios from "axios";

// const override = css`
//   display: block;
//   margin: 0 auto;
//   border-color: red;
// `;

const Home = () => {
  const initSession = JSON.parse(localStorage.getItem("data")) || 0;
  const [dataSession, setDataSession] = useState(initSession);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log(dataSession);
  }, []);

  const loadings = () => {
    let i = 0;
    setTimeout(
      () => {
        return ( <ClipLoader color={"#fffff"} loading={loading} size={150} />);
      },
      initSession ? 3000 : i++
    );
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.clear();
    localStorage.removeItem("data");
  };

  return (
    <>
      {loading ? (
        loadings()
      ) : (
        <>
          <p>Home</p>

          <form onSubmit={handleLogout}>
            <button className="btn btn-danger">Logout</button>
          </form>
        </>
      )}
    </>
  );
};

export default Home;
