import axios from "axios";

async function deleteData(id, text, rating = 0) {
    return axios.put(
      process.env.REACT_APP_BACKEND_PORT + "/api/comment/update/" + id,
      {
        comment_text: text,
        comment_rating: rating,
      }
    );
  }

async function updateRating(id, text, rating = 0) {
  return axios.put(
    process.env.REACT_APP_BACKEND_PORT + "/api/comment/update/" + id,
    {
      comment_text: text,
      comment_rating: rating,
    }
  );
}

export default { updateRating };
