import { DATA_COMMENT } from "./action";

const initialState = {
  dataComment: [
    {
      id: 0,
    },
  ],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case DATA_COMMENT:
      return [...state.dataComment, action.payload];
    default:
      return state;
  }
};

export default reducer;
