import React, { useEffect, useState } from "react";
import ClipLoader from "react-spinners/ClipLoader";
import axios from "axios";

const Home = () => {
  const initSession = JSON.parse(localStorage.getItem("data")) || 0;
  const [dataSession, setDataSession] = useState(initSession);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log(dataSession);

    setLoading(true);
    setTimeout(
      () => {
        setLoading(false);
      },
      initSession ? 1000 : 86400000
    );
  }, []);

  // const loadings = () => {
  //   setTimeout(() => {
  //     return <ClipLoader color={"#ffff"} loading={loading} size={300} />;
  //   }, 3000);
  //   initSession ? setLoading(false) : setLoading(true);
  // };

  const handleLogout = () => {
    // localStorage.clear();
    // localStorage.removeItem("data");
    window.location.href = "http://localhost:3000/login";
  };

  return (
    <>
      {loading ? (
        <ClipLoader color={"#ffff"} loading={"#F37A24"} size={50} />
      ) : (
        <>
          <p>Home</p>

          <form onSubmit={handleLogout}>
            <button className="btn btn-danger">Logout</button>
          </form>
        </>
      )}
    </>
  );
};

export default Home;
