import axios from "axios";

export async function deleteDataComment(id) {
  return axios.delete(
    process.env.REACT_APP_BACKEND_PORT + "/api/comment/delete/" + id
  );
}

export async function createDataComment(text, post_id, name = "Admin") {
  return axios.post(process.env.REACT_APP_BACKEND_PORT + "/api/comment/add", {
    comment_name: name,
    comment_text: text,
    post_id,
  });
}

export async function updateRatingComment(id, text, rating = 0) {
  console.log('rating', rating);
  return axios.put(
    process.env.REACT_APP_BACKEND_PORT + "/api/comment/update/" + id,
    {
      comment_text: text,
      comment_rating: rating,
    }
  );
}
